        pt-5
        pb-5
        >
        <v-badge color="sage_app_color" bottom overlap>
          <span slot="badge">
            <v-icon  large>
              edit
            </v-icon>
          </span>
          <v-avatar size="127" color="grey lighten-4">
            <img src="data:image/jpeg;base64," alt="avatar">
