                  :rules="[rules.phoneNumber]"
                  outline
                ></v-text-field>
              </v-flex>
            </v-layout>
            <v-layout row wrap>
              <v-flex>
                <v-text-field
                  label="UserName (optional)"
                  placeholder=""
                  color="purple darken-2"
                  outline
                ></v-text-field>
              </v-flex>
            </v-layout>
            <v-layout row wrap>
              <v-flex
              xs4 offset-xs8
              sm2 offset-sm10
              md3 offset-md9
              lg3 offset-lg9
              xl3 offset-xl9
              >
