            <v-layout row wrap>
              <v-flex>
                <!-- Event notification -->
                <v-switch
                  v-model="eventNotificationSwitch"
                  :prepend-icon="(eventNotificationSwitch)?'notifications_active':'notifications_off'"
                  label="Event notofications"
                  color="indigo darken-3"
